mapper.update:
UPDATE
  training.workout_exercises
SET
  repetitions = #{repetitions},
  weight = #{weight}
WHERE
  exercise_id = #{exerciseId}
AND
  workout_id = #{workoutId}
AND
  set_nr = #{setNr}

UPDATE
    training.workout_exercises
SET
  repetitions = #{repetitions},
  duration = #{duration}
WHERE
  exercise_id = #{exerciseId}
AND
  workout_id = #{workoutId}
AND
  set_nr = #{setNr}
